commands:
UDID=$(python3 - <<'PY'
import json, subprocess
data = subprocess.check_output(["xcrun", "simctl", "list", "devices", "available", "-j"])
obj = json.loads(data)
for runtime, devices in obj.get("devices", {}).items():
  for d in devices:
    if d.get("isAvailable") and d.get("name", "").startswith("iPhone"):
      print(d["udid"])
      raise SystemExit(0)
raise SystemExit("No available iPhone simulator found")
PY
) && xcodebuild -project Tally.xcodeproj -scheme TallyTests -destination "platform=iOS Simulator,id=$UDID" CODE_SIGNING_ALLOWED=NO test

